package ie.nok.advertisers.stores

import ie.nok.advertisers.Advertiser
import scala.util.chaining.scalaUtilChainingOps
import zio.ZLayer
import zio.ZIO
import ie.nok.hash.Hasher

object AdvertiserStoreInMemory {

  val live: ZLayer[Any, Nothing, AdvertiserStore] =
    List(
      Advertiser(
        id = Hasher.hash("002231"),
        name = "Fran Grincell Properties",
        pictureUrl =
          "data:image/png;base64,",
        phoneNumbers = List("056 771 5000", "087 853 1850"),
        emailAddresses = List("info@fgproperties.ie"),
        physicalAddresses = List("9 Dean St, Gardens, Kilkenny, R95 CX8F"),
        propertyServicesRegulatoryAuthorityLicenceNumber = "002231"
      )
    )
      .pipe { new AdvertiserStoreInMemory(_) }
      .pipe { ZLayer.succeed }
}

class AdvertiserStoreInMemory(all: List[Advertiser]) extends AdvertiserStore {
  def getByPropertyServicesRegulatoryAuthorityLicenceNumber(
      propertyServicesRegulatoryAuthorityLicenceNumber: String
  ): ZIO[Any, Throwable, Option[Advertiser]] =
    all.find(_.propertyServicesRegulatoryAuthorityLicenceNumber == propertyServicesRegulatoryAuthorityLicenceNumber).pipe { ZIO.succeed }
}
